// Define a testForWeekend function to determine if today is weekday or a weekend day

function testForWeekend() {
    // Create a run variable. Run will be true if today is a weekday.
    // Run is false if today is a weekend day.
    var run = false;
    // Get the day of week
    var rightNow = new GlideDateTime();
    var day = rightNow.getDayofWeekUTC();

    // If today is Saturday or Sunday, set the run variable to false
    if (day == 6 || dat == 7) {
        gs.info("Report not send because today is a weekend day.");
        run = true;
    }
    // Return the value of the run variable
    return run;
}

testForWeekend();
